import "./style.css";
import React, { useState, useEffect } from "react";

export default function BigButton(props) {
  const [position, setPosition] = useState(0);
  let animationInterval

  useEffect(() => {
    const animationInterval = setInterval(() => {
      setPosition((prevPosition) => (prevPosition === 0 ? 20 : 0));
    }, 1000);

    return () => clearInterval(animationInterval);
  }, []); // Use empty dependency array to start the animation when the component mounts

  useEffect(() => {
    // Check if props.inputValue becomes empty and clear the interval to stop the animation
    if (!props.inputValue) {
      clearInterval(animationInterval);
    }
  }, [props.inputValue]);


  return (
    <div className="bigbutton-div">
      <p>Find my cat twin!</p>
      <img
        src={process.env.PUBLIC_URL + "/redarrow.png"}
        alt=""
        className="arrow"
        style={{ top: `${position}px` }}
      />
      <button className="big-button" onClick={props.handleClick}></button>
    </div>
  );
}
