import "./App.css";
import { useState, useEffect } from "react";
import InputBox from "./InputBox";
import BigButton from "./BigButton";

function App() {
  const [inputValue, setInputValue] = useState("");
  const [name, setName] = useState("");
  const [displayData, setDisplayData] = useState([]);

  async function handleApiCall(){
    const response = await fetch(`/api/search?name=${encodeURIComponent(name.toLowerCase())}`);
    const data = await response.json();

    if (data.length > 0) {
      // If name exists, add +1 to times searched and get the updated data
      await fetch(`/api/increment?name=${encodeURIComponent(lowerCaseName)}`, {
        method: "PUT",
      });

      // Get and display updated data
      const updatedResponse = await fetch(`/api/search?name=${encodeURIComponent(lowerCaseName)}`);
      const updatedData = await updatedResponse.json();
      setDisplayData(updatedData);
    } else {
      // If name doesn't exist, post it and get the new data
      await fetch(`/api/add`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name: lowerCaseName }),
      });

      // Get and set the new data
      const newDataResponse = await fetch(`/api/search`);
      const newData = await newDataResponse.json();
      setDisplayData(newData);
    }
}




  function handleChange(event) {
    if (event.target.value.length > 255) {
      event.target.value = "";
    }
    setInputValue(event.target.value);
  }

  function handleClick() {
    if (inputValue !== "") {
      setName(inputValue);
      //search database for name(toLowerCase)
      //if name exists, add +1 to times searched, get and display
      //if name doesnt exist, post, get + setDisplayData
    }
  }

  useEffect(() => {
    console.log(inputValue);
  }, [inputValue]);

  return (
    <div className="App">
      <InputBox handleChange={handleChange} maxLength="255"></InputBox>
      <BigButton handleClick={handleClick}></BigButton>
    </div>
  );
}

export default App;
